query AuthConfig {
  me {
    ...UserFields
    __typename
  }
  addresses {
    edges {
      node {
        id
        address
        __typename
      }
      __typename
    }
    __typename
  }
  config {
    streamToken
    __typename
  }
  workspaces {
    edges {
      ...WorkspaceEdgeSimpleFields
      node {
        ...WorkspaceSimpleFields
        members {
          totalCount
          __typename
        }
        groups {
          totalCount
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment UserFields on User {
  ...UserCommonFields
  workspaceProfiles {
    ...WorkspaceProfileFields
    __typename
  }
  __typename
}

fragment UserCommonFields on User {
  id
  addressDomains
  avatarURL
  bio
  isRegistered
  name
  workspaceIDs
  __typename
}

fragment WorkspaceProfileFields on WorkspaceProfile {
  id
  workspace {
    ... on WorkspacePreview {
      ...WorkspacePreviewSimpleFields
      __typename
    }
    __typename
  }
  slackUser {
    ...SlackUserFields
    __typename
  }
  __typename
}

fragment WorkspacePreviewSimpleFields on WorkspacePreview {
  ...WorkspacePreviewCommonFields
  __typename
}

fragment WorkspacePreviewCommonFields on WorkspacePreview {
  id
  name
  domains
  avatarURL
  joinableBy
  memberCount
  __typename
}

fragment SlackUserFields on SlackUser {
  id
  profileURL
  teamID
  __typename
}

fragment WorkspaceEdgeSimpleFields on WorkspaceEdge {
  ...WorkspaceEdgeCommonFields
  __typename
}

fragment WorkspaceEdgeCommonFields on WorkspaceEdge {
  id
  cursor
  memberRole
  __typename
}

fragment WorkspaceSimpleFields on Workspace {
  ...WorkspaceCommonFields
  __typename
}

fragment WorkspaceCommonFields on Workspace {
  id
  admin {
    ...UserFields
    __typename
  }
  name
  domains
  avatarURL
  memberCount
  messageableBy
  chatMessageableBy
  joinableBy
  subscriptionPeriodEnd
  subscriptionStatus
  __typename
}
