query:
query FetchGroupOrPreviewEdge($id: ID!, $groupsFilter: GroupsFilter, $groupsOrder: GroupsOrder = name, $groupsLimit: Int = 100, $groupsAfter: String, $membersLimit: Int = 100, $membersAfter: String, $membersFilter: MembersFilter, $pendingApprovalsLimit: Int = 100, $pendingApprovalsAfter: String) {
  node(id: $id) {
    id
    ... on GroupEdge {
      ...GroupEdgeFields
      node {
        ...GroupFields
        __typename
      }
      __typename
    }
    ... on GroupPreviewEdge {
      ...GroupPreviewEdgeFields
      node {
        ...GroupPreviewFields
        __typename
      }
      __typename
    }
    ... on WorkspaceEdge {
      ...WorkspaceEdgeFields
      node {
        ...WorkspaceFields
        __typename
      }
      __typename
    }
    ... on WorkspacePreviewEdge {
      ...WorkspacePreviewEdgeFields
      node {
        ...WorkspacePreviewFields
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment GroupEdgeFields on GroupEdge {
  ...GroupEdgeCommonFields
  persistentChatEdge {
    node {
      ...ThreadSimpleFields
      __typename
    }
    ...ThreadEdgeFields
    __typename
  }
  __typename
}

