query FetchUsers($after: String, $filter: UsersFilter, $first: Int, $order: UsersOrder) {
  users(after: $after, filter: $filter, first: $first, order: $order) {
    edges {
      ...UserEdgeFields
      node {
        ...UserFields
        __typename
      }
      __typename
    }
    pageInfo {
      endCursor
      hasNextPage
      __typename
    }
    __typename
  }
}

