query:
query MailboxCounts {
  mentionedThreads: threads(filter: {mailbox: mentioned}) {
    totalCount
    __typename
  }
  unreadInbox: threads(filter: {mailbox: unread}) {
    totalCount
    __typename
  }
  unseenFeed: threads(filter: {mailbox: unseen, excludePersistentChats: true}) {
    totalCount
    __typename
  }
}
