query:
query PersistentChats($chatType: ChatType = group, $last: Int, $before: String, $order: ThreadsOrder) {
  persistentChats(
    filter: {chatType: $chatType}
    last: $last
    before: $before
    order: $order
  ) {
    totalCount
    unreadCounts {
      ...UnreadCountFields
      __typename
    }
    pageInfo {
      startCursor
      endCursor
      hasPreviousPage
      hasNextPage
      __typename
    }
    edges {
      ...ThreadEdgeFields
      node {
        ...ThreadSimpleFields
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment UnreadCountFields on UnreadCounts {
  mentioned
  total
  unseen
  __typename
}

fragment ThreadEdgeFields on ThreadEdge {
  id
  cursor
  recipientRole
  isArchived
  remindAt
  isStarred
  isRead
  isSeen
  lastReadID
  lastSeenID
  subscription
  aiSessionID
  unreadMessageCounts {
    ...UnreadCountFields
    __typename
  }
  __typename
}

fragment ThreadSimpleFields on Thread {
  ...ThreadCommonFields
  __typename
}

fragment ThreadCommonFields on Thread {
  id
  subject
  isPersistentChat
  isSlack
