  lastMessage {
    ...MessageFields
    __typename
  }
  messageableBy
  recipients {
    edges {
      node {
        ...RecipientFields
        __typename
      }
      __typename
    }
    pageInfo {
      hasNextPage
      endCursor
      __typename
    }
    totalCount
    __typename
  }
  recipientsAddable
  workspaceID
  createdAt
  closedAt
  closedBy {
    ...UserFields
    __typename
  }
  __typename
}

