query:
query RecentlySharedLinks($filter: LinksFilter, $last: Int, $before: String) {
  links(filter: $filter, last: $last, before: $before) {
    ...LinkConnectionFields
    __typename
  }
}

fragment LinkConnectionFields on LinkConnection {
  edges {
    node {
      ...LinkFields
      __typename
    }
    __typename
  }
  totalCount
  pageInfo {
    hasPreviousPage
    startCursor
    __typename
  }
  __typename
}

fragment LinkFields on Link {
  id
  linked {
    ... on User {
      ...UserFields
      __typename
    }
    ... on ExternalObject {
      ...ExternalObjectFields
      __typename
    }
    ... on File {
      ...FileFields
      __typename
    }
    __typename
  }
  message {
    ...MessagePreviewFields
    __typename
  }
  __typename
}

fragment UserFields on User {
  ...UserCommonFields
  workspaceProfiles {
    ...WorkspaceProfileFields
    __typename
  }
  __typename
}

fragment UserCommonFields on User {
  id
  addressDomains
  avatarURL
  bio
  isRegistered
  name
  workspaceIDs
  __typename
}

fragment WorkspaceProfileFields on WorkspaceProfile {
  id
  workspace {
    ... on WorkspacePreview {
      ...WorkspacePreviewSimpleFields
      __typename
    }
    __typename
  }
  slackUser {
    ...SlackUserFields
    __typename
  }
  __typename
}

fragment WorkspacePreviewSimpleFields on WorkspacePreview {
  ...WorkspacePreviewCommonFields
  __typename
}

fragment WorkspacePreviewCommonFields on WorkspacePreview {
  id
  name
  domains
  avatarURL
  joinableBy
  memberCount
  __typename
}

fragment SlackUserFields on SlackUser {
  id
  profileURL
  teamID
  __typename
}

fragment ExternalObjectFields on ExternalObject {
  id
  url
  objectType
  title
  description
  previewText
  statusText
  siteName
  layout
  icon {
    url
    __typename
  }
  statusIcon {
    url
    __typename
  }
  image {
    url
    blurHash
    __typename
  }
  video {
    url
    contentType
    __typename
  }
  audio {
    url
    contentType
    __typename
  }
  __typename
}

fragment FileFields on File {
  id
  url
  name
  fileType
  contentType
  contentLength
  metadata {
    width
    height
    blurHash
    __typename
  }
  previewable
  __typename
}

fragment MessagePreviewFields on MessagePreview {
  id
  createdAt
  threadID
  user {
    ...UserFields
    __typename
  }
  __typename
}
