query UserSettings {
  settings {
    ...SettingsFields
    __typename
  }
}

fragment SettingsFields on UserSettings {
  ...SettingsSimpleFields
  workSchedule {
    ...WorkScheduleFields
    __typename
  }
  __typename
}

fragment SettingsSimpleFields on UserSettings {
  id
  enterToSend
  llmSettings {
    chatModel
    __typename
  }
  location
  notifyMessages
  notifyMessagesSound
  timezone {
    auto
    name
    __typename
  }
  __typename
}

fragment WorkScheduleFields on WorkSchedule {
  enabled
  sun {
    end
    start
    __typename
  }
  mon {
