query WorkspaceSubscriptionQuery($workspaceID: ID!) {
  workspaceSubscription(workspaceID: $workspaceID) {
    ...WorkspaceSubscriptionFields
    __typename
  }
}

fragment WorkspaceSubscriptionFields on WorkspaceSubscription {
  id
  plan {
    ...SubscriptionPlanFields
    __typename
  }
  status
  quantity
  workspaceID
  cancelAtPeriodEnd
  currentPeriodStart
  currentPeriodEnd
  entitlements {
    ...EntitlementFields
    __typename
  }
  latestInvoice {
    ...InvoiceFields
    __typename
  }
  nextInvoice {
    ...InvoiceFields
    __typename
  }
  __typename
}

fragment SubscriptionPlanFields on SubscriptionPlan {
  id
  name
  description
  interval
  amount
  currency
  __typename
}

fragment EntitlementFields on SubscriptionEntitlements {
  features
  capabilities {
    ...SubscriptionCapabilityFields
    __typename
  }
  __typename
}

fragment SubscriptionCapabilityFields on SubscriptionCapability {
  name
  value
  limit
  __typename
}

fragment InvoiceFields on Invoice {
  id
  periodStart
  periodEnd
  description
  total
  currency
  status
  __typename
}
